import { useState } from 'react';
import './App.css';

function App() {
    const [todos, setTodos] = useState([1]);

    const handleButtonClick = () =>{
      setTodos(['new todo',...todos]);
    }
  return (
    <div className="App">
      <h1>Todolist</h1>
      <input type="text" placeholder='Add todo...'/>
      <button onClick={handleButtonClick}> + </button>
      {
        todos
      }
    </div>
  );
}

export default App;
