import './App.css';

function App() {
    const [inputTodo, setInputTodo] = useState("");
    const [todoList, setTodoList] = useState([]);
    const handleSubmit = (e) => {
      e.prevevtDefault();
      if(inputTodo !== ""){
        setTodoList([{ id: `${inputTodo}-${Date.now()}`, inputTodo,...todoList}])
      }
    }

  return (
    <div className="App">
      <h1>Todolist</h1>
      <form className='toDoForm' onSubmit={handleSubmit}>

      </form>


    </div>
  );
}

export default App;
